import React, { useState, useEffect } from 'react';
import firebase from './firebase'; // Make sure the Firebase configuration is correct
import { useHistory } from 'react-router-dom';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    role: '', // User role (admin, cook, or customer)
  });

  const history = useHistory();
  const user = firebase.auth().currentUser;

  // Fetch user data from Firestore
  useEffect(() => {
    if (user) {
      // Fetch user data from Firestore
      const fetchUserData = async () => {
        try {
          const userDoc = await firebase.firestore().collection('users').doc(user.uid).get();
          if (userDoc.exists) {
            setUserData(userDoc.data());
            setFormData({
              name: userDoc.data().name || '',
              email: userDoc.data().email || '',
              phone: userDoc.data().phone || '',
              role: userDoc.data().role || '',
            });
            setLoading(false);
          }
        } catch (error) {
          console.error("Error fetching user data:", error);
          setLoading(false);
        }
      };
      fetchUserData();
    } else {
      history.push('/login'); // Redirect to login if not authenticated
    }
  }, [user, history]);

  // Handle form input changes
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle profile update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      await firebase.firestore().collection('users').doc(user.uid).update(formData);
      alert('Profile updated successfully!');
      setEditing(false); // Switch to view mode after update
    } catch (error) {
      console.error("Error updating profile:", error);
      alert('Error updating profile');
    }
  };

  // Render loading state or profile data
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile">
      <h2>User Profile</h2>

      {editing ? (
        <form onSubmit={handleProfileUpdate}>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              disabled
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Role:
            <input
              type="text"
              name="role"
              value={formData.role}
              onChange={handleInputChange}
              disabled
            />
          </label>
          <button type="submit">Update Profile</button>
        </form>
      ) : (
        <div>
          <p><strong>Name:</strong> {userData.name}</p>
          <p><strong>Email:</strong> {userData.email}</p>
          <p><strong>Phone:</strong> {userData.phone}</p>
          <p><strong>Role:</strong> {userData.role}</p>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </div>
      )}
    </div>
  );
};

export default Profile;
