// src/components/AdminDashboard.js
import React, { useState, useEffect } from "react";
import { db } from "../firebase/firebaseConfig";

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const orderSnapshot = await db.collection("orders").get();
      setOrders(orderSnapshot.docs.map(doc => doc.data()));
    };

    const fetchUsers = async () => {
      const userSnapshot = await db.collection("users").get();
      setUsers(userSnapshot.docs.map(doc => doc.data()));
    };

    fetchOrders();
    fetchUsers();
  }, []);

  const handleOrderUpdate = async (orderId, status) => {
    // Update order status
    await db.collection("orders").doc(orderId).update({ status });
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      <h2>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.email}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Orders</h2>
      <table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Status</th>
            <th>Customer</th>
            <th>Cook</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.status}</td>
              <td>{order.customerId}</td>
              <td>{order.cookId}</td>
              <td>
                <button onClick={() => handleOrderUpdate(order.id, "completed")}>Complete</button>
                <button onClick={() => handleOrderUpdate(order.id, "cancelled")}>Cancel</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
