import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const CookProfile = () => {
  const [cookData, setCookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [dishes, setDishes] = useState([]);
  const [formData, setFormData] = useState({ name: '', phone: '' });

  const navigate = useNavigate();
  const user = auth.currentUser;

  useEffect(() => {
    if (user) {
      const fetchCookData = async () => {
        try {
          const cookDoc = await firestore.collection('users').doc(user.uid).get();
          if (cookDoc.exists && cookDoc.data().role === 'cook') {
            const data = cookDoc.data();
            setCookData(data);
            setFormData({ name: data.name || '', phone: data.phone || '' });

            // Fetch dishes
            const dishesSnapshot = await firestore.collection('dishes').where('cookId', '==', user.uid).get();
            setDishes(dishesSnapshot.docs.map(doc => doc.data()));
          } else {
            navigate('/login');
          }
          setLoading(false);
        } catch (error) {
          console.error("Error fetching cook data:", error);
          setLoading(false);
        }
      };
      fetchCookData();
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await firestore.collection('users').doc(user.uid).update(formData);
      alert('Profile updated!');
      setEditing(false);
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleAddDish = async () => {
    const dishName = prompt('Enter the dish name');
    if (dishName) {
      try {
        await firestore.collection('dishes').add({ name: dishName, cookId: user.uid, createdAt: new Date() });
        setDishes([...dishes, { name: dishName }]);
      } catch (error) {
        console.error("Error adding dish:", error);
      }
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h2>Cook Profile</h2>
      {editing ? (
        <form onSubmit={handleUpdate}>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} /></label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={(e) => setFormData({ ...formData, phone: e.target.value })} /></label>
          <button type="submit">Save</button>
        </form>
      ) : (
        cookData && (
          <div>
            <p><strong>Name:</strong> {cookData.name}</p>
            <p><strong>Email:</strong> {cookData.email}</p>
            <p><strong>Phone:</strong> {cookData.phone}</p>
            <button onClick={() => setEditing(true)}>Edit Profile</button>
          </div>
        )
      )}

      <h3>Your Dishes</h3>
      <button onClick={handleAddDish}>Add New Dish</button>
      <ul>{dishes.map((dish, index) => <li key={index}>{dish.name}</li>)}</ul>
    </div>
  );
};

export default CookProfile;
