import React, { useState } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { firestore } from "../firebase/firebaseConfig"; // Import firestore
import { useNavigate } from "react-router-dom";
import { doc, setDoc } from "firebase/firestore"; // Import Firestore functions

const Signup = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState(""); // Add name field
  const [role, setRole] = useState(""); // Add role field
  const navigate = useNavigate();

  const handleSignup = () => {
    const auth = getAuth();
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        const user = userCredential.user;
        console.log("User signed up:", user);

        // Save user details to Firestore
        setDoc(doc(firestore, "users", user.uid), {
          name,
          email,
          role,
          uid: user.uid,
        })
          .then(() => {
            console.log("User data saved to Firestore");
            alert("Signup successful!");
            navigate("/home"); // Navigate after successful signup
          })
          .catch((error) => {
            console.error("Error saving data to Firestore:", error);
            alert("Error saving user data!");
          });
      })
      .catch((error) => {
        console.error("Error during signup:", error.message);
        alert(error.message);
      });
  };

  return (
    <div>
      <h2>Signup</h2>
      <input
        type="text"
        placeholder="Enter name"
        value={name}
        onChange={(e) => setName(e.target.value)} // Handle name change
      />
      <input
        type="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Enter password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter role"
        value={role}
        onChange={(e) => setRole(e.target.value)} // Handle role change
      />
      <button onClick={handleSignup}>Sign Up</button>

      <p>
        Already a member?{" "}
        <span
          style={{ color: "blue", cursor: "pointer" }}
          onClick={() => navigate("/login")}
        >
          Login here
        </span>
      </p>
    </div>
  );
};

export default Signup;
