// src/components/SearchDishes.js
import React, { useState, useEffect } from "react";
import { db } from "../firebase/firebaseConfig";

const SearchDishes = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [dishes, setDishes] = useState([]);

  useEffect(() => {
    const fetchDishes = async () => {
      let query = db.collection("dishes");
      if (searchQuery) {
        query = query.where("dishName", ">=", searchQuery).where("dishName", "<=", searchQuery + "\uf8ff");
      }
      const dishSnapshot = await query.get();
      setDishes(dishSnapshot.docs.map(doc => doc.data()));
    };

    fetchDishes();
  }, [searchQuery]);

  return (
    <div>
      <input 
        type="text" 
        value={searchQuery} 
        onChange={(e) => setSearchQuery(e.target.value)} 
        placeholder="Search for dishes"
      />
      <div>
        {dishes.map((dish) => (
          <div key={dish.dishName}>
            <h3>{dish.dishName}</h3>
            <p>{dish.description}</p>
            <p>Price: ${dish.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchDishes;
