import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const CustomerProfile = () => {
  const [customerData, setCustomerData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '' });
  const [dishes, setDishes] = useState([]);

  const navigate = useNavigate();
  const user = auth.currentUser;

  useEffect(() => {
    if (user) {
      const fetchCustomerData = async () => {
        try {
          const customerDoc = await firestore.collection('users').doc(user.uid).get();
          if (customerDoc.exists && customerDoc.data().role === 'customer') {
            const data = customerDoc.data();
            setCustomerData(data);
            setFormData({ name: data.name || '', phone: data.phone || '' });

            // Fetch all available dishes
            const dishesSnapshot = await firestore.collection('dishes').get();
            setDishes(dishesSnapshot.docs.map(doc => doc.data()));
          } else {
            navigate('/login');
          }
          setLoading(false);
        } catch (error) {
          console.error("Error fetching customer data:", error);
          setLoading(false);
        }
      };
      fetchCustomerData();
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await firestore.collection('users').doc(user.uid).update(formData);
      alert('Profile updated!');
      setEditing(false);
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h2>Customer Profile</h2>
      {editing ? (
        <form onSubmit={handleUpdate}>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} /></label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={(e) => setFormData({ ...formData, phone: e.target.value })} /></label>
          <button type="submit">Save</button>
        </form>
      ) : (
        customerData && (
          <div>
            <p><strong>Name:</strong> {customerData.name}</p>
            <p><strong>Email:</strong> {customerData.email}</p>
            <p><strong>Phone:</strong> {customerData.phone}</p>
            <button onClick={() => setEditing(true)}>Edit Profile</button>
          </div>
        )
      )}

      <h3>Available Dishes</h3>
      <ul>{dishes.map((dish, index) => <li key={index}>{dish.name}</li>)}</ul>
    </div>
  );
};

export default CustomerProfile;
