import React, { useState, useEffect } from "react";
import { firestore } from "../firebase/firebaseConfig";
import './admindashboard.css';

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const orderSnapshot = await firestore.collection("orders").get();
      setOrders(orderSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
    };

    const fetchUsers = async () => {
      const userSnapshot = await firestore.collection("users").get();
      setUsers(userSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
    };

    fetchOrders();
    fetchUsers();
  }, []);

  const handleOrderUpdate = async (orderId, status) => {
    await firestore.collection("orders").doc(orderId).update({ status });
    setOrders((prevOrders) =>
      prevOrders.map((order) => (order.id === orderId ? { ...order, status } : order))
    );
  };

  const handleDelete = async (collectionName, docId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this?");
    if (confirmDelete) {
      try {
        await firestore.collection(collectionName).doc(docId).delete();
        alert("Deleted successfully!");
        if (collectionName === "users") {
          setUsers(users.filter((user) => user.id !== docId));
        } else if (collectionName === "orders") {
          setOrders(orders.filter((order) => order.id !== docId));
        }
      } catch (error) {
        console.error("Error deleting:", error);
        alert("Failed to delete.");
      }
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      <h2>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button className="ban" onClick={() => handleDelete("users", user.id)}>Ban</button>
                <button className="delete" onClick={() => handleDelete("users", user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Orders</h2>
      <table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Status</th>
            <th>Customer</th>
            <th>Cook</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.status}</td>
              <td>{order.customerId}</td>
              <td>{order.cookId}</td>
              <td>
                <button className="complete" onClick={() => handleOrderUpdate(order.id, "completed")}>
                  Complete
                </button>
                <button className="cancel" onClick={() => handleOrderUpdate(order.id, "cancelled")}>
                  Cancel
                </button>
                <button className="delete" onClick={() => handleDelete("orders", order.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
