import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig'; 
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    role: '', // User role (admin, cook, or customer)
  });
  const [dishes, setDishes] = useState([]); // Store the list of dishes for cooks

  const navigate = useNavigate(); // Use useNavigate hook for navigation
  const user = auth.currentUser; // Use auth.currentUser

  // Fetch user data from Firestore
  useEffect(() => {
    if (user) {
      // Fetch user data from Firestore
      const fetchUserData = async () => {
        try {
          const userDoc = await firestore.collection('users').doc(user.uid).get(); 
          if (userDoc.exists) {
            const userData = userDoc.data();
            setUserData(userData);
            setFormData({
              name: userData.name || '',
              email: userData.email || '',
              phone: userData.phone || '',
              role: userData.role || '',
            });

            if (userData.role === 'cook') {
              // Fetch dishes if the user is a cook
              const dishesSnapshot = await firestore.collection('dishes').where('cookId', '==', user.uid).get();
              const dishesList = dishesSnapshot.docs.map(doc => doc.data());
              setDishes(dishesList);
            }

            setLoading(false);
          } else {
            console.error("User document not found");
            setLoading(false);
          }
        } catch (error) {
          console.error("Error fetching user data:", error);
          setLoading(false);
        }
      };
      fetchUserData();
    } else {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [user, navigate]);

  // Handle form input changes
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle profile update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      await firestore.collection('users').doc(user.uid).update(formData); 
      alert('Profile updated successfully!');
      setEditing(false); // Switch to view mode after update
    } catch (error) {
      console.error("Error updating profile:", error);
      alert('Error updating profile');
    }
  };

  // Handle adding a dish (for cooks only)
  const handleAddDish = async (e) => {
    e.preventDefault();
    const dishName = prompt('Enter the name of the dish');
    if (dishName) {
      try {
        await firestore.collection('dishes').add({
          name: dishName,
          cookId: user.uid,
          createdAt: new Date(),
        });
        alert('Dish added successfully!');
        // Optionally, fetch dishes again to update the UI
        setDishes([...dishes, { name: dishName }]);
      } catch (error) {
        console.error("Error adding dish:", error);
        alert('Error adding dish');
      }
    }
  };

  // Render loading state or profile data
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile">
      <h2>User Profile</h2>

      {editing ? (
        <form onSubmit={handleProfileUpdate}>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              disabled
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </label>
          <label>
            Role:
            <input
              type="text"
              name="role"
              value={formData.role}
              onChange={handleInputChange}
              disabled
            />
          </label>
          <button type="submit">Update Profile</button>
        </form>
      ) : (
        userData && (
          <div>
            <p><strong>Name:</strong> {userData.name}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Phone:</strong> {userData.phone}</p>
            <p><strong>Role:</strong> {userData.role}</p>
            <button onClick={() => setEditing(true)}>Edit Profile</button>
          </div>
        )
      )}

      {/* Role-Based Functionality */}
      {userData && userData.role === 'cook' && (
        <div>
          <h3>Your Dishes</h3>
          <button onClick={handleAddDish}>Add New Dish</button>
          <ul>
            {dishes.map((dish, index) => (
              <li key={index}>{dish.name}</li>
            ))}
          </ul>
        </div>
      )}

      {userData && userData.role === 'customer' && (
        <div>
          <h3>Available Dishes</h3>
          {/* Display available dishes for customers to purchase */}
          <ul>
            {dishes.map((dish, index) => (
              <li key={index}>{dish.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Profile;
