import React, { useState } from "react";
import { firestore } from "../firebase/firebaseConfig";
import { collection, addDoc } from "firebase/firestore";

const AddDish = ({ cookId, onDishAdded, onCancel }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [quantity, setQuantity] = useState("");

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Store the selected file
  };

  const uploadToImgur = async (file) => {
    const formData = new FormData();
    formData.append("image", file);

    const response = await fetch("https://api.imgur.com/3/image", {
      method: "POST",
      headers: {
        Authorization: "Client-ID 4f2f5b5a0b6dabc", // Replace with your own Imgur Client ID
      },
      body: formData,
    });

    const data = await response.json();
    return data.data.link; // Returns the Imgur URL
  };

  const handleAddDish = async () => {
    if (!name || !price || !quantity || !imageFile) {
      alert("Please fill in all required fields.");
      return;
    }

    if (quantity <= 0) {
      alert("Quantity must be greater than 0.");
      return;
    }

    const imageUrl = await uploadToImgur(imageFile); // Upload image and get URL

    const dish = {
      name,
      description,
      price: parseFloat(price),
      imageUrl, // Store the Imgur URL
      quantity: parseInt(quantity, 10),
      cookId,
      createdAt: new Date(),
    };

    await addDoc(collection(firestore, "dishes"), dish);
    alert("Dish added successfully!");
    onDishAdded();
    onCancel();
  };

  return (
    <>
      <div className="add-dish-overlay" onClick={onCancel}></div>
      <div className="add-dish-modal">
        <h2>Add a New Dish</h2>
        <input type="text" placeholder="Dish Name" value={name} onChange={(e) => setName(e.target.value)} />
        <textarea placeholder="Dish Description" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} />
        <input type="file" accept="image/*" onChange={handleImageChange} /> {/* Image Upload */}
        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} />

        <button onClick={handleAddDish}>Add Dish</button>
        <button onClick={onCancel} className="cancel-btn">Cancel</button>
      </div>
    </>
  );
};

export default AddDish;
