import React, { useState } from "react";
import { firestore } from "../firebase/firebaseConfig";
import "./adddish.css"; // Import the CSS file

const AddDish = ({ onClose }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const handleAddDish = async () => {
    if (!name || !description || !price || !quantity || !imageUrl) {
      alert("Please fill all fields");
      return;
    }

    const dish = {
      name,
      description,
      price,
      quantity,
      imageUrl,
    };

    await firestore.collection("dishes").add(dish);
    alert("Dish added successfully");

    // Clear the form after submission
    setName("");
    setDescription("");
    setPrice("");
    setQuantity("");
    setImageUrl("");
    onClose(); // Close the modal after adding dish
  };

  return (
    <div className="modal-background">
      <div className="modal-box">
        <h2>Add a New Dish</h2>
        <input
          type="text"
          placeholder="Dish Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <textarea
          placeholder="Dish Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Quantity Available"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <input
          type="text"
          placeholder="Image URL"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
        />
        <button onClick={handleAddDish}>Add Dish</button>
        <button className="close-btn" onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default AddDish;
