import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig';
import { useNavigate } from 'react-router-dom';
import { doc, getDoc } from 'firebase/firestore';  // Import the necessary Firestore functions

const CustomerProfile = () => {
  const [customerData, setCustomerData] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const user = auth.currentUser;

  useEffect(() => {
    if (user) {
      const fetchCustomerData = async () => {
        try {
          // Use modular API: Reference to the document
          const userDocRef = doc(firestore, 'users', user.uid);
          const userDoc = await getDoc(userDocRef);  // Use getDoc() to fetch the document

          if (userDoc.exists()) {
            const userData = userDoc.data();
            if (userData.role === 'customer') {
              setCustomerData(userData);
              setLoading(false);
            } else {
              navigate('/login'); // Redirect if not a customer
            }
          } else {
            navigate('/login'); // Redirect if document doesn't exist
          }
        } catch (error) {
          console.error('Error fetching customer data:', error);
          navigate('/login'); // In case of any error, redirect to login
        }
      };
      fetchCustomerData();
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h2>Customer Profile</h2>
      <p><strong>Name:</strong> {customerData.name}</p>
      <p><strong>Email:</strong> {customerData.email}</p>
    </div>
  );
};

export default CustomerProfile;
