import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig';
import { useNavigate } from 'react-router-dom';
import { doc, getDoc, collection, query, where, getDocs } from 'firebase/firestore';  

const CustomerProfile = () => {
  const [customerData, setCustomerData] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const user = auth.currentUser;

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchCustomerData = async () => {
      try {
        const userDocRef = doc(firestore, 'users', user.uid);
        const userDoc = await getDoc(userDocRef);

        if (userDoc.exists()) {
          const userData = userDoc.data();
          if (userData.role === 'customer') {
            setCustomerData(userData);
            fetchOrders(user.uid); // Fetch orders after getting customer data
          } else {
            navigate('/login');
          }
        } else {
          navigate('/login');
        }
      } catch (error) {
        console.error('Error fetching customer data:', error);
        navigate('/login');
      }
    };

    const fetchOrders = async (customerId) => {
      try {
        const ordersQuery = query(collection(firestore, 'orders'), where('customerId', '==', customerId));
        const ordersSnapshot = await getDocs(ordersQuery);
        const ordersList = ordersSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setOrders(ordersList);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCustomerData();
  }, [user, navigate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h2>Customer Profile</h2>
      <p><strong>Name:</strong> {customerData.name}</p>
      <p><strong>Email:</strong> {customerData.email}</p>

      <h3>Your Orders</h3>
      {orders.length > 0 ? (
        <ul className="order-list">
          {orders.map((order) => (
            <li key={order.id} className="order-item">
              <p><strong>Order ID:</strong> {order.id}</p>
              <p><strong>Total Price:</strong> ${order.total}</p>
              <p><strong>Status:</strong> {order.status}</p>
              <h4>Dishes:</h4>
              <ul>
                {order.dishes.map((dish, index) => (
                  <li key={index}>{dish.name} - {dish.quantity} x ${dish.price}</li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      ) : (
        <p>No orders placed yet.</p>
      )}
    </div>
  );
};

export default CustomerProfile;
