import React, { useState, useEffect } from "react";
import { firestore } from "../firebase/firebaseConfig";
import { collection, getDocs, doc, updateDoc, deleteDoc } from "firebase/firestore";

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [dishes, setDishes] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const orderSnapshot = await getDocs(collection(firestore, "orders"));
      setOrders(orderSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
    };

    const fetchUsers = async () => {
      const userSnapshot = await getDocs(collection(firestore, "users"));
      setUsers(userSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
    };

    const fetchDishes = async () => {
      const dishSnapshot = await getDocs(collection(firestore, "dishes"));
      setDishes(dishSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
    };

    fetchOrders();
    fetchUsers();
    fetchDishes();
  }, []);

  // ðŸš€ Update order status
  const handleOrderUpdate = async (orderId, status) => {
    const orderRef = doc(firestore, "orders", orderId);
    await updateDoc(orderRef, { status });
    setOrders(orders.map(order => order.id === orderId ? { ...order, status } : order));
  };

  // ðŸš€ Ban user (update role to 'banned')
  const handleBanUser = async (userId) => {
    const userRef = doc(firestore, "users", userId);
    await updateDoc(userRef, { role: "banned" });
    setUsers(users.map(user => user.id === userId ? { ...user, role: "banned" } : user));
  };

  // ðŸš€ Delete post (dish)
  const handleDeletePost = async (dishId) => {
    await deleteDoc(doc(firestore, "dishes", dishId));
    setDishes(dishes.filter(dish => dish.id !== dishId));
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      {/* ðŸš€ Users Table */}
      <h2>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {user.role !== "banned" && (
                  <button onClick={() => handleBanUser(user.id)}>Ban</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ðŸš€ Orders Table */}
      <h2>Orders</h2>
      <table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Status</th>
            <th>Customer</th>
            <th>Cook</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.status}</td>
              <td>{order.customerId}</td>
              <td>{order.cookId}</td>
              <td>
                <button onClick={() => handleOrderUpdate(order.id, "completed")}>Complete</button>
                <button onClick={() => handleOrderUpdate(order.id, "cancelled")}>Cancel</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ðŸš€ Dishes Table (Delete Posts) */}
      <h2>Posts (Dishes)</h2>
      <table>
        <thead>
          <tr>
            <th>Dish Name</th>
            <th>Price</th>
            <th>Cook</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dishes.map((dish) => (
            <tr key={dish.id}>
              <td>{dish.name}</td>
              <td>${dish.price}</td>
              <td>{dish.cookId}</td>
              <td>
                <button onClick={() => handleDeletePost(dish.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
