import React, { useState } from "react";
import { firestore } from "../firebase/firebaseConfig";
import { storage } from "../firebase/firebaseConfig"; // Import Firebase Storage
import { collection, addDoc } from "firebase/firestore";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import "./adddish.css"; // Import the CSS

const AddDish = ({ cookId, onDishAdded, onCancel }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [quantity, setQuantity] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      setImageFile(e.target.files[0]);
    }
  };

  const handleAddDish = async () => {
    if (!name || !price || !quantity) {
      alert("Please fill in all required fields.");
      return;
    }

    if (quantity <= 0) {
      alert("Quantity must be greater than 0.");
      return;
    }

    let imageUrl = "";

    if (imageFile) {
      setUploading(true);
      const storageRef = ref(storage, `dishImages/${imageFile.name}`);
      const uploadTask = uploadBytesResumable(storageRef, imageFile);

      try {
        await uploadTask;
        imageUrl = await getDownloadURL(storageRef);
      } catch (error) {
        console.error("Error uploading image:", error);
        alert("Failed to upload image. Please try again.");
        setUploading(false);
        return;
      }
    }

    const dish = {
      name,
      description,
      price: parseFloat(price),
      imageUrl,
      quantity: parseInt(quantity, 10),
      cookId,
      createdAt: new Date(),
    };

    try {
      await addDoc(collection(firestore, "dishes"), dish);
      alert("Dish added successfully!");
      onDishAdded(); // Refresh dish list in profile
      onCancel(); // Close the modal after adding
    } catch (error) {
      console.error("Error adding dish:", error);
      alert("Failed to add dish. Please try again.");
    }

    setUploading(false);
  };

  return (
    <>
      <div className="add-dish-overlay" onClick={onCancel}></div>
      <div className="add-dish-modal">
        <h2>Add a New Dish</h2>
        <input type="text" placeholder="Dish Name" value={name} onChange={(e) => setName(e.target.value)} />
        <textarea placeholder="Dish Description" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} />
        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} />

        <input type="file" accept="image/*" onChange={handleImageChange} />

        <button onClick={handleAddDish} disabled={uploading}>
          {uploading ? "Uploading..." : "Add Dish"}
        </button>
        <button onClick={onCancel} className="cancel-btn">Cancel</button>
      </div>
    </>
  );
};

export default AddDish;
