import React, { useState } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { firestore, auth } from "../firebase/firebaseConfig"; // Import firestore here
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState(""); // Add name state
  const [role, setRole] = useState(""); // Add role state
  const navigate = useNavigate();

  const handleSignup = () => {
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        const user = userCredential.user;
        console.log("User signed up:", user);

        // Add user data to Firestore
        firestore
          .collection("users")
          .doc(user.uid) // Use the user's UID as the document ID
          .set({
            name,
            email,
            role, // You can change this based on your use case
            uid: user.uid,
          })
          .then(() => {
            alert("Signup successful and data saved to Firestore!");
            navigate("/home"); // Redirect to homepage after successful signup
          })
          .catch((error) => {
            console.error("Error saving data to Firestore:", error);
            alert("Error saving user data!");
          });
      })
      .catch((error) => {
        console.error("Error during signup:", error.message);
        alert(error.message);
      });
  };

  return (
    <div>
      <h2>Signup</h2>

      <input
        type="text"
        placeholder="Enter name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <input
        type="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="password"
        placeholder="Enter password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <input
        type="text"
        placeholder="Enter role (admin, cook, customer)"
        value={role}
        onChange={(e) => setRole(e.target.value)}
      />

      <button onClick={handleSignup}>Sign Up</button>

      <p>
        Already a member?{" "}
        <span
          style={{ color: "blue", cursor: "pointer" }}
          onClick={() => navigate("/login")}
        >
          Login here
        </span>
      </p>
    </div>
  );
};

export default Signup;
