import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase/firebaseConfig';
import { useNavigate } from 'react-router-dom';
import AddDish from '../components/AddDish';
import { collection, doc, getDoc, getDocs, query, where } from 'firebase/firestore'; // Import necessary Firestore functions

const CookProfile = () => {
  const [cookData, setCookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showAddDish, setShowAddDish] = useState(false);
  const [dishes, setDishes] = useState([]);
  const navigate = useNavigate();
  const user = auth.currentUser;

  useEffect(() => {
    if (user) {
      const fetchCookData = async () => {
        try {
          // Fetch the cook's profile data
          const userDocRef = doc(firestore, 'users', user.uid);
          const userDoc = await getDoc(userDocRef);

          if (userDoc.exists() && userDoc.data().role === 'cook') {
            setCookData(userDoc.data());

            // Fetch the cook's dishes
            const dishesQuery = query(
              collection(firestore, 'dishes'), // Reference the 'dishes' collection
              where('cookId', '==', user.uid) // Filter dishes by cookId
            );
            const dishesSnapshot = await getDocs(dishesQuery); // Use getDocs for query results
            const dishList = dishesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setDishes(dishList);

            setLoading(false);
          } else {
            navigate('/login'); // Redirect if not a cook
          }
        } catch (error) {
          console.error('Error fetching cook data:', error);
          navigate('/login'); // Redirect on error
        }
      };
      fetchCookData();
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  // Callback function to update the dish list after adding a new dish
  const handleDishAdded = async () => {
    const dishesQuery = query(
      collection(firestore, 'dishes'),
      where('cookId', '==', user.uid)
    );
    const dishesSnapshot = await getDocs(dishesQuery);
    const dishList = dishesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setDishes(dishList);
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h2>Cook Profile</h2>
      <p><strong>Name:</strong> {cookData.name}</p>
      <p><strong>Email:</strong> {cookData.email}</p>

      {/* Button to open AddDish form */}
      <button onClick={() => setShowAddDish(!showAddDish)}>
        {showAddDish ? 'Close' : 'Add Dish'}
      </button>

      {/* Show AddDish component when button is clicked */}
      {showAddDish && <AddDish cookId={user.uid} onDishAdded={handleDishAdded} />}

      {/* Display the list of added dishes */}
      <h3>Your Dishes</h3>
      <ul>
        {dishes.map(dish => (
          <li key={dish.id}>{dish.name} - ${dish.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default CookProfile;
